const BASE_URL = "http://localhost/dk_infoway_practical/public";
function postRequest(url, data, cb) {
    $.ajax({
        type: "POST",
        url: BASE_URL + '/api' + url,
        data: data,
        success: function (resultData) {
            cb(resultData,null);
        },
        error: function (error) {
            cb(null,error);
        },
    });
}

function postAuthRequest(url, data, cb) {
    data = {...data,_token:$('#csrf_token').val()}
    console.log(data);
    $.ajax({
        type: "POST",
        url: BASE_URL + '/api' + url,
        data: data,
        headers : {
            'Authorization' : `Bearer ${getCookie('auth_token')}`
        },
        success: function (resultData) {
            cb(resultData,null);
        },
        error: function (error) {
            cb(null,error);
        },
    });
}

function setCookie(name,value,days) {
    var expires = "";
    if (days) {
        var date = new Date();
        date.setTime(date.getTime() + (days*24*60*60*1000));
        expires = "; expires=" + date.toUTCString();
    }
    document.cookie = name + "=" + (value || "")  + expires + "; path=/";
}
function getCookie(name) {
    var nameEQ = name + "=";
    var ca = document.cookie.split(';');
    for(var i=0;i < ca.length;i++) {
        var c = ca[i];
        while (c.charAt(0)==' ') c = c.substring(1,c.length);
        if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length,c.length);
    }
    return null;
}
function eraseCookie(name) {   
    document.cookie = name +'=; Path=/; Expires=Thu, 01 Jan 1970 00:00:01 GMT;';
}


function Toast(msg,flag){
    toastr.options = {
        "closeButton": true,
        "progressBar": true,
        "positionClass": "toast-bottom-right"
    };
    if(flag == 1){
        toastr.success(msg);
    }else if(flag ==0){
        toastr.error(msg);
    }else{
        toastr.warning(msg);
    }
}

function logout(){
    this.postAuthRequest('/user/logout',{},function(res,err){
        if(res.flag){
            eraseCookie('auth_token')
            window.location.href = BASE_URL
        }
    })
}